('App\\Model\\Product', 37, 'bd', 'name', 'Simple Kind To Skin Hydrating Light Moisturiser', 13),
('App\\Model\\Product', 37, 'bd', 'description', '<p>Keep your skin hydrated all day long with Simple&nbsp;Hydrating Light Moisturizer. This light, silky formula provides up to&nbsp;12 hours of hydration&nbsp;and instantly doubles skin&#39;s hydration. It is made with added vitamins and a blend of skin-loving ingredients. This moisturizer contains no dyes, artificial perfumes, or harsh chemicals that can upset skin so it won&#39;t clog pores or feel greasy&nbsp;</p>', 14),
('App\\Model\\Product', 38, 'bd', 'name', 'The Body Shop Vitamin C Glow Boosting Moisturiser', 15),
